INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Partial Interest Transferred', 'PartialInterestTransferred', 'Partial Interest Transferred', 'Character', 'Lookup', 'PARTIAL_INTEREST_TRANSFERRED', 'Whenever LESS than 100% ownership interest is transferred by the recorded instrument. For example, a partner sells only his 50% interest. This also applies to transfers where the Grantors entire ownership interest is LESS than 100%. For example, the Grantor conveys all of his interest in the property, which is a 25% interest. When the percentage is a single digit (1-9), it is preceded with a zero: 5% = 05. When interest shown is a whole number plus a value after the decimal or fraction, it is rounded to nearest whole number only: 75.5% = 76; 33 1/3% = 33, etc.  See "Code Desc" Tab for other possible field values.  Note: If there are two or more parties each conveying a partial interest, where the total interest conveyed equals 100% - NOT a partial interest.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('FIPS Code', 'FIPSCode', 'FIPS Code', 'Character', '', '', 'Numeric code which identifies every County/State in the United States. First 2 bytes = State; last 3 = County.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('BKFS Internal PID', 'BKFSInternalPID', 'BKFS Internal PID', 'Character', '', '', 'Auto-assigned internal ID for reference only.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Assessor’s Parcel Number', 'Assessor’sParcelNumber', 'Assessor’s Parcel Number', 'Character', '', '', 'Assessor''s Parcel Number or Parcel Identification Number. An arbitrary parcel identifier assigned by the county Assessor to simplify identification and expedite the locating of parcels. While format and composition vary widely, it is usually composed of MAP, PAGE, PARCEL, and SUB PARCEL numbers. Some counties use SEC‑TWP‑RNG as part of the APN or a MAP-BOOK, PAGE, and PARCEL reference; others use SUBDIVISION, BLOCK, and LOT to identify a parcel.  Leading zeros or spaces used to maintain the integrity of the format. Re-formatted if necessary to match the county display format.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Duplicate APN', 'DuplicateAPN', 'Duplicate APN', 'Character', '', '', 'A) Separate records are created for each "duplicate" APNs with a different property address.  B) Whenever a Duplicate APN with a different address exists, a sequential number (beginning with "01") is entered in this field for each duplicate record. This assigned ID number creates a UNIQUE parcel ID for each record when combined with the parcel number in Field #3.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Address Source Flag', 'PropertyAddressSourceFlag', 'Property Address Source Flag', 'Character', 'Lookup', 'PROPERTY_ADDRESS_SOURCE_FLAG', 'Indicates property address source. Blank by default. See "Code Desc" Tab.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Full Street Address', 'PropertyFullStreetAddress', 'Property Full Street Address', 'Character', '', '', 'Street Address components are:  • House Number  • House Alpha (i.e. 400 ½, 230A, etc)  • Street Direction Left (i.e. N, S, E, W, NE, SE, etc)  • Street Name  • Suffix (Ave, Rd, St, etc)  • Street Direction Right (i.e. N, S, E, W, NE, SE etc)  Example incorporating all components: 400 ½ E Iris Ave NE  Note: Rural Routes are considered to be Property Addresses.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property City Name', 'PropertyCityName', 'Property City Name', 'Character', '', '', 'City Name where the property is located. May be different from Legal: City, Municipality, Township (Field #64).', 'blackknight', 'county', 'tax');
