INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Zip Code', 'PropertyZipCode', 'Property Zip Code', 'Character', '', '', 'May be obtained from mailing address for owner‑occupied. Refer to Property Address Source Flag (Field #5).', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Zip + 4', 'PropertyZip+4', 'Property Zip + 4', 'Character', '', '', '4‑digit extension to postal zip code for the property address. Not always available.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Unit Type', 'PropertyUnitType', 'Property Unit Type', 'Character', 'Lookup', 'ADDRESS_UNIT_TYPE', 'Property Address Unit Designator. The list on the "Code Desc" Tab is a selected portion for our purposes only. Refer to the USPS Postal Addressing Standards book - Publication #28 dated 8/95.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Unit Number', 'PropertyUnitNumber', 'Property Unit Number', 'Character', '', '', 'Unit or suite number. Part of the Property Address. Also may be present in mailing address. Often used to identify condo or apartment units. May be numeric or alpha or both. May be different from Legal: Unit (Field #60).   EXAMPLES: Unit 5, or #5, or Number 5, or Unit Five - entered as: 5', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property House Number', 'PropertyHouseNumber', 'Property House Number', 'Character', '', '', 'House number location of the property. In some areas, house numbers may include a direction designation.  EXAMPLE: 123N456 MAIN ST', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Street Direction Left', 'PropertyStreetDirectionLeft', 'Property Street Direction Left', 'Character', '', '', 'Appears before the street name and denotes the street name direction.  VALID DIRECTIONS: N, S, E, W and NE, NW, SE, SW  EXAMPLE: 3550 W Temple St', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Street Name', 'PropertyStreetName', 'Property Street Name', 'Character', '', '', 'Property Street Name', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Street Suffix', 'PropertyStreetSuffix', 'Property Street Suffix', 'Character', 'Lookup', 'STREET_SUFFIX', 'The recommended Official USPS Standardized Suffix Abbreviations. The list on the "Code Desc" Tab is a selected portion for our purposes only. Refer to the USPS Postal Addressing Standards book - Publication #28 dated 8/95 for additional abbreviations.', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Street Direction Right', 'PropertyStreetDirectionRight', 'Property Street Direction Right', 'Character', '', '', 'In some cities (Seattle), the directional appears afterthe street suffix to denote direction/quadrant. EXAMPLE: 12345 Main St N Both directions may be present: 12345 N Main St NW', 'blackknight', 'county', 'tax');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Address: Carrier Route', 'PropertyAddress:CarrierRoute', 'Property Address: Carrier Route', 'Character', '', '', 'Format: C999', 'blackknight', 'county', 'tax');
