INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Assessee Mail: City Name', 'AssesseeMail:CityName', 'Assessee Mail: City Name', 'Character', '', '', 'In the event the mailing address is a foreign country, City & Country information will be present here.  Example: ONTARIO CANADA MW1 XY2.  The foreign mail code is dropped if it exceeds the field length. City & Country names not truncated.', 'blackknight', 'county', 'tax');
