INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('California Homeowner''s Exemption', 'CaliforniaHomeowner''sExemption', 'California Homeowner''s Exemption', 'Character', '', '', 'Indicates whether (H)omeowner''s Exemption is applicable. Not the same as Homestead - see Tax Exemption = "D" (Field #40). Blank for deed updates.', 'blackknight', 'county', 'tax');
