INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Standardized Land Use Code', 'StandardizedLandUseCode', 'Standardized Land Use Code', 'Character', '', '', 'Land Use code standardized for all counties. See Separate Land Use Table.  NOTE: Code “1999” based on the following criteria:  1. Land Use in the source file is generally indicated as residential. This may vary from county to county, i.e.: R = residential, Lots, etc.  2. Improvement value is greater than zero.  3. There is Homeowner’s exemption or the property is Owner Occupied.  4. If there is no land use designation, Bedroom and Bath fields are populated.', 'blackknight', 'county', 'tax');
