INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Main Building Area', 'MainBuildingArea', 'Main Building Area', 'Character', '', '', 'The building area of the primary structure on the property.  When multiple building areas are given for a structure, the preferred data for this field is Living Area for residential properties and Total Area for all others.', 'blackknight', 'county', 'tax');
