INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Recorder’s Document Number', 'Recorder’sDocumentNumber', 'Recorder’s Document Number', 'Character', '', '', 'Sequential number assigned to documents at the time of recording for identification and to establish the order of recordation.', 'blackknight', 'county', 'deed');
