INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Multi-APN Flag', 'Multi-APNFlag', 'Multi-APN Flag', 'Character', 'Lookup', 'MULTI_APN_FLAG', 'Indicates the number of APNs (2-9) appearing on the conveying instrument. Left blank if only one parcel. See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#1 - Seller First Name & Middle Name', '#1-SellerFirstName&MiddleName', '#1 - Seller First Name & Middle Name', 'Character', '', '', 'The first name and middle name(when present) without abbreviations, or first name and middle initial (if that is how it appears on the document).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#1 - Seller Last Name OR Corporation Name', '#1-SellerLastNameORCorporationName', '#1 - Seller Last Name OR Corporation Name', 'Character', '', '', 'If the last name is composed of two names, both are entered in the sequence they appear separated with a space, e.g. Mary B. Ashley Taylor, entered as: ASHLEY TAYLOR  Last names such as Mc Neil, O''Brien, etc. are entered with no space, apostrophe or any other punctuation e.g. OBRIEN, MCNEIL.  Names followed by 2nd, 3rd, or 4th are entered in the last name field after the name as roman numerals: 2nd = SMITH II, 3rd = SMITH III, 4th = SMITH IV.  All other title designations are entered after the last name, such as: JR, SR, ESQ, PHD  If Seller Name is blurred or missing from document, this field will show:   NOT PROVIDED', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#1 - Seller ID Code', '#1-SellerIDCode', '#1 - Seller ID Code', 'Character', 'Lookup', 'ID_CODE', 'Used to identify the nature of each grantor keyed from the recorded document. See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#2 - Seller First Name & Middle Name', '#2-SellerFirstName&MiddleName', '#2 - Seller First Name & Middle Name', 'Character', '', '', 'See definition for #1 - Seller First Name & Middle Name (Field #25).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#2 - Seller Last Name OR Corporation Name', '#2-SellerLastNameORCorporationName', '#2 - Seller Last Name OR Corporation Name', 'Character', '', '', 'See definition for #1 - Seller Last Name or Corporation (Field #26).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#2 - Seller ID Code', '#2-SellerIDCode', '#2 - Seller ID Code', 'Character', 'Lookup', 'ID_CODE', 'Used to identify the nature of each grantor keyed from the recorded document. See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#1 - Buyer First Name & Middle Name', '#1-BuyerFirstName&MiddleName', '#1 - Buyer First Name & Middle Name', 'Character', '', '', 'The first name and middle name (when present) without abbreviations, or first name and middle initial (if that is how it appears on the document).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#1 - Buyer Last Name OR Corporation Name', '#1-BuyerLastNameORCorporationName', '#1 - Buyer Last Name OR Corporation Name', 'Character', '', '', 'If the last name is composed of two names, both are entered in the sequence they appear separated with a space, e.g. Mary B. Ashley Taylor, entered as: ASHLEY TAYLOR  Last names such as Mc Neil, O''Brien, etc. are entered with no space, apostrophe or any other punctuation e.g. OBRIEN, MCNEIL.  Names followed by 2nd, 3rd, or 4th are entered in the last name field after the name as roman numerals: 2nd = SMITH II, 3rd = SMITH III, 4th = SMITH IV.  All other title designations are entered after the last name, such as: JR, SR, ESQ, PHD  If Buyer Name is blurred or missing from document, this field will show:   NOT PROVIDED', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#1 - Buyer ID Code', '#1-BuyerIDCode', '#1 - Buyer ID Code', 'Character', 'Lookup', 'ID_CODE', 'Used to identify the nature of each grantee keyed from the recorded document. See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#2 - Buyer First Name & Middle Name', '#2-BuyerFirstName&MiddleName', '#2 - Buyer First Name & Middle Name', 'Character', '', '', 'See definition for #1 - Buyer First Name & Middle Name (Field #28).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('#2 - Buyer Last Name OR Corporation Name', '#2-BuyerLastNameORCorporationName', '#2 - Buyer Last Name OR Corporation Name', 'Character', '', '', 'See definition for #1 - Buyer Last Name or Corporation (Field #29).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Buyer Vesting Code', 'BuyerVestingCode', 'Buyer Vesting Code', 'Character', 'Lookup', 'BUYER_BORROWER_VESTING_CODE', 'The vesting code, when present, indicates how the buyers took title to the subject property. See "Code Desc" Tab.   Note: The vesting codes EA and EU should not appear on records being keyed by IDM since we will be picking up all Buyer names involved in the transaction. However, these codes may still apply for transfer data purchased from a 3rd party vendor.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Concurrent TD: Document Number or Book/Page', 'ConcurrentTD:DocumentNumberorBook/Page', 'Concurrent TD: Document Number or Book/Page', 'Character', '', '', 'The sequential Document number or the Book/Page number assigned to the Concurrent Mortgage at the time of recording for identification and to establish the order of recordation.  Note1: The Document number takes priority if both Document number and Book and Page are recorded on the document.  Note2: If Book and Page values are keyed, separate the values with a slash (''/'') Example: "4778/456" = Book 4778, Page 456', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Buyer Mail: City', 'BuyerMail:City', 'Buyer Mail: City', 'Character', '', '', 'See Assessment Tab - Assessee Mail: City Name (Field #28).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Buyer Mail: State', 'BuyerMail:State', 'Buyer Mail: State', 'Character', '', '', 'Two-letter standard postal abbreviation.  XX = Foreign Mail Address', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Buyer Mail: Zip Code', 'BuyerMail:ZipCode', 'Buyer Mail: Zip Code', 'Character', '', '', 'Buyer Mail: Zip Code', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Buyer Mail: Zip + 4', 'BuyerMail:Zip+4', 'Buyer Mail: Zip + 4', 'Character', '', '', 'Four-digit extension to postal zip code for the mailing address. Not always available.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Lot Code', 'Legal:LotCode', 'Legal: Lot Code', 'Character', 'Lookup', 'LEGAL_LOT_CODE', 'Identifies properties which include more than one lot or partial lots. See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Lot Number(s)', 'Legal:LotNumber(s)', 'Legal: Lot Number(s)', 'Character', '', '', 'The individual lot(s) which comprise the property. The actual lot number(s) such as in a tract or subdivision. If more than one, as many multiple lot numbers as allowed by the field length are entered, separated with a comma (,) or ampersand (&). A hyphen is used to indicate a range (-). Refer to Lot Code (Field #39).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Section', 'Legal:Section', 'Legal: Section', 'Character', '', '', 'The section of the city in which the property is located. Not the same as the “Section” of a Township-Range. See Legal: Sec/Twn/Rng/Mer (Field #51).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: District', 'Legal:District', 'Legal: District', 'Character', '', '', 'The district in which the property is located. Usually a numeric code corresponding to the literal name in Legal: City, Municipality, Township (Field #46).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Land Lot', 'Legal:LandLot', 'Legal: Land Lot', 'Character', '', '', 'A large portion or tract of land (which may also encompass many individual blocks or lots) in which the property is located.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Unit', 'Legal:Unit', 'Legal: Unit', 'Character', '', '', 'The subdivision unit number. Common for condominiums, townhomes, etc. Not necessarily the same as the Property Unit Number (Field #8).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: City, Township, Municipality', 'Legal:City,Township,Municipality', 'Legal: City, Township, Municipality', 'Character', '', '', 'The jurisdiction in which the property is located. May be “Unincorporated”, if applicable. Often a description of the code provided in Legal: District (Field #43). Not necessarily the same as the Property City (Field #3).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Subdivision Name', 'Legal:SubdivisionName', 'Legal: Subdivision Name', 'Character', '', '', 'The name of the subdivision, plat, or tract in which the property is located.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Phase Number', 'Legal:PhaseNumber', 'Legal: Phase Number', 'Character', '', '', 'Generally, the phase of a subdivision or tract development.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Tract Number', 'Legal:TractNumber', 'Legal: Tract Number', 'Character', '', '', 'The number of the tract in which the property is located. Followed by "POR" (portion of) when applicable.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Brief Description', 'Legal:BriefDescription', 'Legal: Brief Description', 'Character', '', '', 'Lists exceptions, when only a portion of Block, Lot, Tract or Subdivision is entered in the specific field. Or, when legal description components are not fielded, this field will be populated. Refer to Complete Legal Description Code (Field #75)', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Legal: Section/Township/Range/Meridian', 'Legal:Section/Township/Range/Meridian', 'Legal: Section/Township/Range/Meridian', 'Character', '', '', 'Format: SEC 99 TWN 99A RNG 99A  Where "A" = Directional (N/S for TWN and E/W for RNG)', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Recorder''s Map Reference', 'Recorder''sMapReference', 'Recorder''s Map Reference', 'Character', '', '', 'Standardized to identify each portion. Multiples indicated with the ampersand (&), and ranges separated with a hyphen (-). Depending on the length of the Map Reference, spaces may be omitted.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property/Buyer Mailing Address Code', 'Property/BuyerMailingAddressCode', 'Property/Buyer Mailing Address Code', 'Character', 'Lookup', 'PROPERTY_BUYER_MAILING_ADDRESS_CODE', 'See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Property Use Code', 'PropertyUseCode', 'Property Use Code', 'Character', 'Lookup', 'PROPERTY_USE_CODE', 'Property use as indicated on the trust deed (mortgage) and/or attached Rider. See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Original Date of Contract', 'OriginalDateofContract', 'Original Date of Contract', 'DateTime', '', '', 'The date that the document was executed by the parties. Pre-dates the recording date. In some cases, may be the Notary Date. YYYYMMDD', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Sales Price', 'SalesPrice', 'Sales Price', 'Character', '', '', 'Total Sales Price relating to all properties conveyed by the corresponding instrument. If more than one parcel was included on the conveying instrument there is no way to allot the price to each parcel. Whenever the recorded document (deed) indicates the actual sales price, it will be entered here in whole dollars - see Sales Price Code = "D" (Field #57).', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Sales Price Code', 'SalesPriceCode', 'Sales Price Code', 'Character', 'Lookup', 'SALES_PRICE_CODE_DEED', 'See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('City Transfer Tax', 'CityTransferTax', 'City Transfer Tax', 'Character', '', '', 'Applies in only designated cities where they levy a city tax on all purchase money real estate transfers within their jurisdiction. Input Format: 99999v99 (implied decimal)', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('County Transfer Tax', 'CountyTransferTax', 'County Transfer Tax', 'Character', '', '', 'Generally appears in the form of a transfer tax stamp on the deed. The stamp amount represents a rate levied by the state per $1,000 of consideration paid in the transaction. Depending on the individual state, the stamp amount when converted to the actual transaction amount, using the corresponding rate, may represent the full sales price or only the amount of equity transferred. i.e. cash down plus any new financing. Input Format: 99999v99 (implied decimal)', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Total Transfer Tax', 'TotalTransferTax', 'Total Transfer Tax', 'Character', '', '', 'Unique to certain regions where you add the city and county transfer tax amounts to compute the full sales price. Input Format: 999999v99 (implied decimal)', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Concurrent TD: Lender Name (Beneficiary)', 'ConcurrentTD:LenderName(Beneficiary)', 'Concurrent TD: Lender Name (Beneficiary)', 'Character', '', '', 'Name of the beneficiary to the first trust deed (mortgage).  When more that one Lender is reported, only the first Lender appearing on the document is entered. If a Trust, along with Trustees appear as Lender, then only the Trust Name is entered.  Note: Whenever we encounter a mortgage/trust deed document that also references an Assignment of Mortgage, our practice is to capture the name of the Lender that ORIGINATED the loan, rather than the name of the Assignee Lender.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Concurrent TD: Lender Type', 'ConcurrentTD:LenderType', 'Concurrent TD: Lender Type', 'Character', 'Lookup', 'LENDER_TYPE', 'See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Concurrent TD: Loan Amount', 'ConcurrentTD:LoanAmount', 'Concurrent TD: Loan Amount', 'Character', '', '', 'Mortgage (purchase money) recorded concurrently with the conveying instrument. Reported in dollars only, no commas (,). Not calculated when loan made in installments distributed on a monthly or annual basis.  Note: On Assumed Loans (Field #64 = "A"), the loan amount being assumed is entered here, if available.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Concurrent TD: Loan Type', 'ConcurrentTD:LoanType', 'Concurrent TD: Loan Type', 'Character', 'Lookup', 'LOAN_TYPE', 'Indicates the type of loan, if able to determine from the recorded document. See "Code Desc" Tab.', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Concurrent TD: Type Financing', 'ConcurrentTD:TypeFinancing', 'Concurrent TD: Type Financing', 'Character', 'Lookup', 'TYPE_FINANCING', 'When available will indicate the type of interest rate terms for the concurrent TD AND/OR see Loan Type (Field #64). Left blank if unknown. See "Code Desc" Tab.  Note:  1. When document indicates the loan is initiated at an Adjustable Rate, Type Financing will be "ADJ".  2. When document indicates the loan is initiated with a Fixed Rate, Type Financing will be "FIX".', 'blackknight', 'county', 'deed');
INSERT INTO config_data_source_fields ("SystemName", "ShortName", "LongName", "DataType", "Interpretation", "LookupName", "Description", data_source_id, data_source_type, data_list_type) VALUES ('Concurrent TD: Interest Rate', 'ConcurrentTD:InterestRate', 'Concurrent TD: Interest Rate', 'Character', '', '', 'When available, initial rate as given on the trust deed or rider. Input Format: 99v99 (implied decimal)', 'blackknight', 'county', 'deed');
